mapper.insert:

	SELECT BOARD_SEQ.NEXTVAL
	  FROM DUAL

INSERT INTO BOARD_T (
BOARD_NO
, BOARD_TITLE
, BOARD_CONTENT
, USER_NO
, BOARD_CREATE_DT
, BOARD_MODIFY_DT
) VALUES (
#{boardNo}
, #{boardTitle}
, #{boardContent}
, #{user.userNo}
, CURRENT_TIMESTAMP
, CURRENT_TIMESTAMP
)

mapper.update:
UPDATE BOARD_T
   SET BOARD_HIT = BOARD_HIT + 1
 WHERE BOARD_NO = #{boardNo}

UPDATE BOARD_T
   SET BOARD_TITLE = #{boardTitle}
   	 , BOARD_CONTENT = #{boardContent}
     , BOARD_MODIFY_DT = CURRENT_TIMESTAMP
 WHERE BOARD_NO = #{boardNo}

UPDATE BOARD_T
   SET BOARD_TITLE = #{boardTitle}
  	 , BOARD_CONTENT = #{boardContent}
   	 , BOARD_MODIFY_DT = TO_CHAR(CURRENT_DATE,'YYYY-MM-DD HH24:MI:SS)
 WHERE BOARD_NO = #{boardNo}
