mapper.update:
UPDATE USER_T 
SET USER_NAME = #{userName}, 
	USER_GENDER = #{userGender}, 
	USER_MOBILE = #{userMobile}
WHERE USER_NO = #{userNo}

UPDATE USER_T 
SET USER_PW = #{newPw}, 
	PW_MODIFY_DT = CURRENT_DATE
WHERE USER_NO = #{userNo} 
	AND USER_PW = #{originPw}

mapper.delete:
UPDATE USER_T 
SET WITHDRAWAL_DT = CURRENT_DATE
WHERE USER_NO = #{userNo} 
	AND USER_PW = #{originPw}

mapper.select:
SELECT COUNT(*)
  FROM BOARD_T
 WHERE USER_NO = #{userNo}

SELECT COUNT(*)
  FROM COMMENT_T
 WHERE USER_NO = #{userNo}
