mapper.insert:
INSERT INTO USER_T (
					  USER_NO
				  , USER_EMAIL
				  , USER_PW
				  , USER_NAME
				  , USER_GENDER
				  , USER_MOBILE
				  , SIGNUP_DT
) VALUES (
					USER_SEQ.NEXTVAL
				  , #{userEmail}
				  , #{userPw}
				  , #{userName}
				  , #{userGender}
				  , #{userMobile}
				  , CURRENT_DATE
)

    INSERT INTO ACCESS_HISTORY_T (
				        ACCESS_HISTORY_NO
				      , USER_EMAIL
				      , ACCESS_IP
				      , USER_AGENT
				      , SESSION_ID
				      , LOGIN_DT
				      , LOGOUT_DT
    ) VALUES (
				        ACCESS_HISTORY_SEQ.NEXTVAL
				      , #{userEmail}
				      , #{accessIp}
				      , #{userAgent}
				      , #{sessionId}
				      , CURRENT_DATE
				      , NULL
    )

mapper.update:
UPDATE ACCESS_HISTORY_T
   SET LOGOUT_DT = CURRENT_DATE
 WHERE SESSION_ID = #{sessionId}

mapper.select:
SELECT USER_NO, USER_EMAIL, USER_PW, USER_NAME, USER_GENDER, USER_MOBILE, PW_MODIFY_DT, SIGNUP_DT, USER_UPLOAD_PATH, USER_IMG
  FROM USER_T

   USER_EMAIL = #{userEmail}
  AND USER_PW= #{userPw}
